11. void getInput( int N) { 
 for (int i=1;i<=N;i++) { 
 scanf("%d", &input[i]); 
 excess[i] = excess[i -1]; 
 if (input[i] == 1) { 
 excess[i]++; 
 } else { 
 excess[i] --; 
 } 
 } 
} 
