Pipe innerPipe = DP[i -2][balance(j+ 1)]; 
Pipe outerPipe = setPipeLength(i -1, 
innerPipe.height+ 1); 
DP[i][j] = addPipe(innerPipe, outerPipe); 
} 
for (int k=2;k<i;k+= 2) { 
if (seimbang(k,j)) { 
Pipe minPipe = addPipe(DP[k][j], DP[i -
k][balance(j+k)]); 
DP[i][j] = leastLength(DP[i][j], minPipe); 
} 
} 
} 
} 
} 
} 
